SELECT articles.id, articles.uuid, articles.title, articles.abstract, articles.place_of_publication, articles.journal, articles.journal_abrevated, articles.publication_date, articles.electronic_publication_date, articles.pages, articles.journal_volume, articles.journal_issue, articles.pubmed_id, articles.pmc_id, articles.pii, articles.doi, articles.print_issn, articles.electronic_issn, articles.linking_issn, articles.nlm_journal_id, articles.created_at, articles.created_by, articles.updated_at, articles.updated_by 
FROM articles JOIN article_authors ON articles.id = article_authors.article_id JOIN authors ON authors.id = article_authors.author_id JOIN article_keywords AS article_keywords_1 ON articles.id = article_keywords_1.article_id JOIN keywords ON keywords.id = article_keywords_1.keyword_id JOIN keywords AS keywords_1 ON keywords_1.id = article_keywords_1.keyword_id 
WHERE lower(articles.title) LIKE lower('%blindness%') OR lower(articles.journal) LIKE lower('%blindness%') OR lower(articles.title) LIKE lower('%gupta%') OR lower(articles.journal) LIKE lower('%gupta%') OR lower(authors."fullName") LIKE lower('%blindness%') OR lower(authors."fullName") LIKE lower('%gupta%') OR lower(keywords_1.keyword) LIKE lower('%blindness%') OR lower(keywords_1.keyword) LIKE lower('%gupta%')